        #region Singleton Members
        // Single instance
        private static ResponseCodeHelper ThisInstance = null;

        // Private constructor
        private ResponseCodeHelper() { }

        public static ResponseCodeHelper Instance()
        {
            if (ThisInstance == null)
            {
                ThisInstance = new ResponseCodeHelper();
            }

            return ThisInstance;
        }
        #endregion

